/**
 * @param type - "evm" or "solana", extend this union as needed (e.g., "sui")
 * @param id - Chain ID, optional for EVM
 */
type Chain = EvmChain | SolanaChain | AptosChain | ChromiaChain | FuelChain | SuiChain;
type SuiChain = {
    type: "sui";
};
type EvmChain = {
    type: "evm";
    id: number;
};
type SolanaChain = {
    type: "solana";
};
type AptosChain = {
    type: "aptos";
};
type ChromiaChain = {
    type: "chromia";
};
type FuelChain = {
    type: "fuel";
};

export type { AptosChain, Chain, ChromiaChain, EvmChain, FuelChain, SolanaChain, SuiChain };
